const EventPractice = () => {
    const [username, setUsername] = usetState('');
    const [message, setMessage] = usetState('');
    const onChangeusername = e => setUsername(e.target.vaue);
    const onChangeMessage = e => setMessage(e.target.value);
    const onClick = () => {
        alert(username + ': ' + message);
        setUsername('');
        setMessage('');
    };
    const onKeyPress = e => {
        if (e.key === 'Enter') {
            onClick();
        }
    };
    return (
        <div>

        </div>
    );
};

export default EventPractice;
