import React, { useState } from 'react';

const EventPractice = () => {
    const [username, setUsername] = useState('');
    const [message, setMessage] = useState('');
    const onChangeusername = e => setUsername(e.target.vaue);
    const onChangeMessage = e => setMessage(e.target.value);
    const onClick = () => {
        alert(username + ': ' + message);
        setUsername('');
        setMessage('');
    };
    const onKeyPress = e => {
        if (e.key === 'Enter') {
            onClick();
        }
    };
    return (
        <div>
            <h1>이벤트 연습</h1>
            <hr />
            <input type="text" name="username" placeholder="사용자명" value={username} onChange={onChangeusername} />
            <input type="text" name="message" placeholder="아무거나 입력해 보세요" value={message} onChange={onChangeMessage} onKeyPress={onKeypress} />
            <button onClick={onClick}>확인</button>
        </div>
    );
};

export default EventPractice;
